import React, { useEffect, useState } from 'react';
import '../styles/PopupPage.css';

const PopupPage = ({ doctorId, onClose }) => {
  const [doctorDetails, setDoctorDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchDoctorDetails = async () => {
      try {
        setLoading(true);
        setError(null);
        // Replace with the actual API call for fetching doctor details
        const response = await fetch(`http://127.0.0.1:9000/api/doctors/${doctorId}/`);
        const data = await response.json();
        setDoctorDetails(data);
      } catch (err) {
        console.error('Error fetching doctor details:', err);
        setError('Failed to fetch doctor details. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchDoctorDetails();
  }, [doctorId]);

  if (!doctorId) return null;

  return (
    <div className="popup-backdrop">
      <div className="popup-container">
        {loading ? (
          <p>Loading doctor details...</p>
        ) : error ? (
          <p className="error-message">{error}</p>
        ) : (
          <>
            <button className="popup-close-btn" onClick={onClose}>
              &times;
            </button>
            <h2>{doctorDetails.name}</h2>
            <p>City: {doctorDetails.city}</p>
            <p>Degree: {doctorDetails.degree}</p>
            <p>Experience: {doctorDetails.experience} years</p>
            <p>Top Expertise: {doctorDetails.top_expertise}</p>
            {doctorDetails.services.length > 0 && (
              <p>Services: {doctorDetails.services.join(', ')}</p>
            )}
            <p>Hospital: {doctorDetails.hospital}</p>
            <p>Fees: ${doctorDetails.fees}</p>
          </>
        )}
      </div>
    </div>
  );
};

export default PopupPage;
