import React, { useState } from 'react';
import '../styles/dashboard.css';
import Profile from './Profile'; // Import Profile component for rendering
import Chat from './Chat'; // Import Chat component (create similarly if needed)
import Settings from './Settings'; // Import Settings component (create similarly if needed)
import Appointments from './Appointments'; // Import Appointments component (create similarly if needed)

const Dashboard = () => {
  const [activeComponent, setActiveComponent] = useState('profile');

  const renderContent = () => {
    switch (activeComponent) {
      case 'profile':
        return <Profile />;
      case 'chat':
        return <Chat />;
      case 'settings':
        return <Settings />;
      case 'appointments':
        return <Appointments />;
      default:
        return <div>Select an option from the menu</div>;
    }
  };

  return (
    <div className="dashboard-container">
      <nav className="sidebar">
        <ul>
          <li onClick={() => setActiveComponent('profile')}>Profile</li>
          <li onClick={() => setActiveComponent('chat')}>Chat</li>
          <li onClick={() => setActiveComponent('settings')}>Settings</li>
          <li onClick={() => setActiveComponent('appointments')}>Appointments</li>
        </ul>
      </nav>
      <div className="content">
        {renderContent()}
      </div>
    </div>
  );
};

export default Dashboard;
