import React, { useEffect, useState, useCallback } from 'react';
import Navbar from '../components/Navbar';
import '../styles/FindDoctor.css';

const FindDoctor = () => {
  const [doctors, setDoctors] = useState([]);
  const [page, setPage] = useState(1);

  const fetchDoctors = useCallback(async () => {
    try {
      const response = await fetch(`http://127.0.0.1:8000/api/doctor/all-doctor-profiles/?page=${page}`);
      const data = await response.json();
      setDoctors((prevDoctors) => [...prevDoctors, ...data]);
    } catch (error) {
      console.error('Error fetching doctors:', error);
    }
  }, [page]);

  const handleScroll = useCallback(() => {
    const { scrollTop, scrollHeight, clientHeight } = document.documentElement;
    if (scrollHeight - scrollTop === clientHeight) {
      setPage((prevPage) => prevPage + 1);
    }
  }, []);

  useEffect(() => {
    fetchDoctors();
  }, [fetchDoctors]);

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, [handleScroll]);

  return (
    <div className="find-doctor">
      <Navbar />
      <div className="welcome-section">
        <h1>Welcome to Find Doctor</h1>
        <p>Browse our directory of skilled medical professionals.</p>
      </div>
      <div className="doctor-cards-container">
        {doctors.map((doctor, index) => (
          <div key={index} className="doctor-card">
            <img
              src={doctor.profile_pic || 'https://via.placeholder.com/150'}
              alt={doctor.name}
              className="doctor-profile-pic"
            />
            <h3>{doctor.name}</h3>
            <p>{doctor.degree}</p>
            <p>Experience: {doctor.experience} years</p>
            <p>Expertise: {doctor.top_expertise}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default FindDoctor;
