import React from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Login from "./accounts/components/Login";
import Signup from "./accounts/components/Signup";
import Dashboard from "./dashboard/components/Dashboard";
import Profile from "./dashboard/components/Profile";
import UpdateProfile from "./dashboard/components/UpdateProfile";


const isAuthenticated = () => {
  const token = localStorage.getItem("accessToken");
  // You can also check token expiration here if you need
  return !!token;
};

function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<Navigate to="/login" replace />} />
        <Route path="/login" element={<Login />} />
        <Route path="/signup" element={<Signup />} />

        {/* Protect dashboard route with authentication check */}
        <Route
          path="/dashboard/*"
          element={isAuthenticated() ? <Dashboard /> : <Navigate to="/login" replace />}
        />
        {/* Add route for Profile page */}
        <Route
          path="/profile"
          element={isAuthenticated() ? <Profile /> : <Navigate to="/login" replace />}

        />
        <Route path="/update-profile" component={() => <UpdateProfile userId={userId} token={token} />} />
      </Routes>
    </Router>
  );
}

export default App;
