import React, { useEffect, useState } from "react";
import { Routes, Route, Navigate } from "react-router-dom";
import Navbar from "./components/Navbar";
import Welcome from "./components/Welcome";
import Profile from "./components/Profile";
import Settings from "./components/Settings";

const Dashboard = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const token = localStorage.getItem("accessToken");
    const userId = localStorage.getItem("userId");

    console.log("Checking authentication...");
    console.log("Token:", token);
    console.log("User ID:", userId);

    // Only set isAuthenticated to true if both token and userId are present
    if (token && userId) {
      setIsAuthenticated(true);
    } else {
      console.log("User is not authenticated.");
    }
    setLoading(false);
  }, []);

  if (loading) {
    console.log("Loading user data...");
    return <p>Loading...</p>; // Show loading until we finish the auth check
  }

  console.log("Rendering Dashboard with authentication:", isAuthenticated);

  return (
    <div className="dashboard">
      <Navbar />
      <div className="dashboard-content">
        <Routes>
          <Route path="/" element={<Welcome />} />
          {isAuthenticated ? (
            <>
              <Route path="/profile" element={<Profile />} />
              <Route path="/settings" element={<Settings />} />
            </>
          ) : (
            <Route path="*" element={<Navigate to="/" replace />} /> // Redirect to home if not authenticated
          )}
          {/* If user is not authenticated, redirect all other routes to home */}
          <Route path="*" element={<Navigate to="/" replace />} />
        </Routes>
      </div>
    </div>
  );
};

export default Dashboard;
