import React, { useState } from "react";
import { useNavigate } from "react-router-dom"; // To navigate after successful login
import { login } from "../services/api"; // Assuming you have a login function in your api.js file

const Login = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    username: "",
    password: "",
  });
  const [message, setMessage] = useState("");

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await login(formData); // Send the login request

      // Ensure we store accessToken and userId in localStorage
      localStorage.setItem("accessToken", response.access);  // Store access token
      localStorage.setItem("refreshToken", response.refresh);  // Store refresh token
      localStorage.setItem("userId", response.user.id);  // Store user ID

      // Optionally, verify if these are being saved correctly by logging them
      console.log("Stored Tokens:", localStorage.getItem("accessToken"), localStorage.getItem("userId"));

      // Navigate user to profile or dashboard based on their role
      if (response.user.user_type === "doctor") {
        navigate("/dashboard/profile");  // Adjust path as needed
      } else if (response.user.user_type === "user") {
        navigate("/dashboard/user");
      } else {
        setMessage("Unknown user type.");
      }
    } catch (error) {
      console.error("Login error:", error);
      setMessage(error.message || "Login failed.");
    }
  };

  return (
    <div className="login-form">
      <h2>Login</h2>
      {message && <p>{message}</p>} {/* Display any messages */}
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="username"
          placeholder="Username"
          value={formData.username}
          onChange={handleChange}
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          value={formData.password}
          onChange={handleChange}
        />
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default Login;
