import axios from "axios";
const API_BASE_URL = "http://127.0.0.1:8000/api";

// Get doctor profile
export const getDoctorProfile = async () => {
  try {
    const token = localStorage.getItem("accessToken");  // Get the token
    const userId = localStorage.getItem("userId");  // Get the user ID

    console.log("Token retrieved:", token);
    console.log("User ID retrieved:", userId);

    if (!token || !userId) {
      console.error("Error: Missing token or user ID.");
      throw new Error("Missing token or user ID.");
    }

    console.log(`Fetching profile for user ID: ${userId}...`);
    const response = await axios.get(`${API_BASE_URL}/doctor/doctor-profiles/${userId}/`, {
      headers: {
        Authorization: `Bearer ${token}`,  // Authorization header
        "Content-Type": "application/json",
      },
    });

    console.log("Profile data fetched successfully:", response.data);
    return response.data;
  } catch (error) {
    console.error("Error fetching profile:", error.message);
    throw error.response?.data || "Failed to fetch the profile. Please check your API or network.";
  }
};
