import React, { useEffect, useState } from 'react';
import Navbar from '../components/Navbar';
import '../styles/FindDoctor.css';
import { fetchDoctors } from '../services/api'; // API logic

const FindDoctor = () => {
  const [doctors, setDoctors] = useState([]);
  const [page, setPage] = useState(1);
  const [loading, setLoading] = useState(false);

  const loadDoctors = async () => {
    setLoading(true);
    try {
      const response = await fetchDoctors(page);
      setDoctors((prev) => [...prev, ...response]);
      setPage((prev) => prev + 1);
    } catch (error) {
      console.error('Error fetching doctors:', error);
    } finally {
      setLoading(false);
    }
  };

  // Fetch doctors on initial render
  useEffect(() => {
    loadDoctors();
  }, []); // Dependency array is empty as loadDoctors is stable

  // Infinite scrolling logic
  const handleScroll = () => {
    if (
      window.innerHeight + document.documentElement.scrollTop ===
        document.documentElement.offsetHeight &&
      !loading
    ) {
      loadDoctors();
    }
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, [loading]); // Add `loading` as a dependency

  return (
    <div className="find-doctor">
      <Navbar />
      <div className="welcome-container">
        <h1>Welcome to Find a Doctor</h1>
        <p>Discover the best doctors near you and their expertise.</p>
      </div>
      <div className="cards-container">
        {doctors.map((doctor, index) => (
          <div key={index} className="doctor-card">
            <div className="profile-pic">
              {doctor.profile_pic ? (
                <img src={doctor.profile_pic} alt={doctor.name} />
              ) : (
                <div className="placeholder-pic">No Image</div>
              )}
            </div>
            <div className="doctor-info">
              <h3>{doctor.name}</h3>
              <p>Degree: {doctor.degree}</p>
              <p>Experience: {doctor.experience} years</p>
              <p>Expertise: {doctor.top_expertise}</p>
              {doctor.services.length > 0 && (
                <p>Services: {doctor.services.join(', ')}</p>
              )}
            </div>
          </div>
        ))}
        {loading && <div className="loading">Loading...</div>}
      </div>
    </div>
  );
};

export default FindDoctor;
