import React, { useState, useEffect } from "react";
import { Routes, Route, useNavigate } from "react-router-dom";
import Layout from "./Layout";
import { fetchDashboardData } from "../services/api";

const UserDashboard = () => {
  const [welcomeMessage, setWelcomeMessage] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const getData = async () => {
      try {
        const data = await fetchDashboardData("user");
        setWelcomeMessage(data.message || "Welcome to the User Dashboard!");
      } catch (error) {
        console.error("Error fetching dashboard data:", error);
      }
    };
    getData();
  }, []);

  const menuItems = [
    { name: "Profile", path: "profile" },
    // Future options can be added here
  ];

  const handleMenuClick = (path) => {
    navigate(`/dashboard/user/${path}`);
  };

  return (
    <Layout menuItems={menuItems} onMenuClick={(item) => handleMenuClick(item.path)}>
      <Routes>
        <Route
          path="/profile"
          element={<h1>Profile coming soon!</h1>} // Add profile component later
        />
        <Route
          path="/"
          element={<h1>{welcomeMessage}</h1>}
        />
      </Routes>
    </Layout>
  );
};

export default UserDashboard;
