import axios from "axios";

const DASHBOARD_API_BASE_URL = "http://127.0.0.1:8000/api/dashboard";
const DOCTOR_API_BASE_URL = "http://127.0.0.1:8000/api/doctor";

// Common method for handling API requests
const apiRequest = async (url, method = "GET", data = null, headers = {}) => {
  try {
    const options = {
      method,
      url,
      headers: {
        Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
        ...headers,
      },
      ...(data && { data }),
    };
    const response = await axios(options);
    return response.data;
  } catch (error) {
    throw error.response?.data || `Failed to fetch data from ${url}`;
  }
};

// Fetch dashboard data (common for user and doctor)
export const fetchDashboardData = async (userType) => {
  const endpoint = userType === "doctor" ? "doctor/" : "user/";
  return apiRequest(`${DASHBOARD_API_BASE_URL}/${endpoint}`);
};

// Fetch doctor profile data
export const fetchDoctorProfile = async (profileId) => {
  return apiRequest(`${DOCTOR_API_BASE_URL}/doctor-profiles/${profileId}/`);
};

// Fetch update data
export const fetchDoctorUpdateData = async () => {
  return apiRequest(`${DOCTOR_API_BASE_URL}/updates/`);
};

// Fetch notifications data
export const fetchNotifications = async () => {
  return apiRequest(`${DOCTOR_API_BASE_URL}/notifications/`);
};

// Fetch chat data
export const fetchChats = async () => {
  return apiRequest(`${DOCTOR_API_BASE_URL}/chats/`);
};

// Fetch appointments data
export const fetchAppointments = async () => {
  return apiRequest(`${DOCTOR_API_BASE_URL}/appointments/`);
};

// Fetch settings data
export const fetchSettings = async () => {
  return apiRequest(`${DOCTOR_API_BASE_URL}/settings/`);
};
