import axios from "axios";

const API_BASE_URL = "http://127.0.0.1:8000/api/dashboard";

// Fetch dashboard data (doctor or user)
export const fetchDashboardData = async (userType) => {
  try {
    const response = await axios.get(`${API_BASE_URL}/dashboard/`, {
      headers: {
        // Example: Include authorization if needed
        Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
      },
      params: { user_type: userType },
    });
    return response.data;
  } catch (error) {
    throw error.response?.data || "Failed to fetch dashboard data.";
  }
};
