import React, { useState, useEffect } from "react";
import { getDoctorProfile } from "../services/api";
import "../styles/Profile.css";

const Profile = () => {
  const [profile, setProfile] = useState(null);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(true); // To handle loading state

  const fetchProfile = async () => {
    console.log("Fetching profile data...");
    try {
      const profileData = await getDoctorProfile();
      console.log("Fetched Profile Data:", profileData);
      setProfile(profileData); // Set profile data
    } catch (error) {
      console.error("Error in fetchProfile:", error);
      setError(error || "Failed to load profile. Please try again.");
    } finally {
      setLoading(false); // Stop the loading spinner
    }
  };

  useEffect(() => {
    console.log("Profile component mounted, fetching profile...");
    fetchProfile(); // Fetch profile when the component is mounted
  }, []);

  // Debugging: Log whenever profile updates
  useEffect(() => {
    if (profile) console.log("Profile updated:", profile);
  }, [profile]);

  // Placeholder image fallback
  const profilePicUrl = profile?.profile_pic || "/images/placeholder.png";

  if (loading) return <p>Loading profile...</p>; // Show loading spinner/message

  return (
    <div className="profile">
      {error ? (
        <p className="error">{error}</p>
      ) : (
        profile && (
          <div>
            <div className="profile-header">
              <div className="profile-pic-wrapper">
                <img src={profilePicUrl} alt="Profile Pic" className="profile-pic" />
              </div>
              <h2>{profile.name || "N/A"}</h2>
            </div>
            <div className="profile-info">
              {[
                { label: "Degree", value: profile.degree },
                { label: "Email", value: profile.email },
                { label: "Phone", value: profile.phone_number },
                { label: "City", value: profile.city },
                { label: "Description", value: profile.description },
                { label: "Hospital", value: profile.hospital },
                { label: "Experience", value: `${profile.experience} years` },
                { label: "Fees", value: profile.fees },
                { label: "Top Expertise", value: profile.top_expertise },
                { label: "Rating", value: profile.rating },
                { label: "Address", value: profile.address },
                { label: "Area", value: profile.area },
              ].map(({ label, value }) => (
                <div key={label} className="profile-info-item">
                  <p className="label">{label}:</p>
                  <p>{value || "N/A"}</p>
                </div>
              ))}
            </div>
          </div>
        )
      )}
    </div>
  );
};

export default Profile;
