import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { login } from "../services/api"; // Ensure this API function is implemented correctly
import "../styles/Auth.css";

const Login = () => {
  const [formData, setFormData] = useState({ email: "", password: "" });
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(false); // Add loading state
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true); // Show loading state during API call
    setMessage(""); // Clear previous messages
    try {
      const response = await login(formData);

      console.log("Login Response:", response); // Debugging response

      // Save tokens to local storage
      localStorage.setItem("accessToken", response.access);
      localStorage.setItem("refreshToken", response.refresh);

      // Redirect based on user type
      if (response.user.user_type === "doctor") {
        navigate("/dashboard");
      } else if (response.user.user_type === "user") {
        navigate("/dashboard/user");
      } else {
        setMessage("Unknown user type.");
      }
    } catch (error) {
      console.error("Login error:", error); // Log error for debugging
      setMessage(error.response?.data?.detail || "Login failed. Please try again.");
    } finally {
      setLoading(false); // Hide loading state
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-card">
        <h2>Login</h2>
        <form onSubmit={handleSubmit}>
          <input
            type="email"
            name="email"
            placeholder="Email"
            value={formData.email}
            onChange={handleChange}
            required
          />
          <input
            type="password"
            name="password"
            placeholder="Password"
            value={formData.password}
            onChange={handleChange}
            required
          />
          <button type="submit" disabled={loading}>
            {loading ? "Logging in..." : "Login"}
          </button>
        </form>
        {message && <p className="message">{message}</p>}
      </div>
    </div>
  );
};

export default Login;
