import React, { useState, useEffect } from "react";
import Layout from "../components/Layout";
import Profile from "./Profile";
import Chat from "./Chat";
import Settings from "./Settings";
import axios from "axios";

const DoctorDashboard = () => {
  const [activeComponent, setActiveComponent] = useState("profile");
  const [profileData, setProfileData] = useState(null);
  const [chatData, setChatData] = useState(null);
  const [settingsData, setSettingsData] = useState(null);

  const menuItems = [
    { name: "Profile", path: "profile" },
    { name: "Chat", path: "chat" },
    { name: "Settings", path: "settings" },
  ];

  useEffect(() => {
    if (activeComponent === "profile") {
      fetchProfileData();
    // } else if (activeComponent === "chat") {
    //   fetchChatData();
    // } else if (activeComponent === "settings") {
    //   fetchSettingsData();
    }
  }, [activeComponent]);

  const fetchProfileData = async () => {
    try {
      const response = await axios.get("/api/profile");
      setProfileData(response.data);
    } catch (error) {
      console.error("Failed to fetch profile data", error);
    }
  };

  const fetchChatData = async () => {
    try {
      const response = await axios.get("/api/chat");
      setChatData(response.data);
    } catch (error) {
      console.error("Failed to fetch chat data", error);
    }
  };

  const fetchSettingsData = async () => {
    try {
      const response = await axios.get("/api/settings");
      setSettingsData(response.data);
    } catch (error) {
      console.error("Failed to fetch settings data", error);
    }
  };

  const renderComponent = () => {
    switch (activeComponent) {
      case "profile":
        return <Profile data={profileData} />;
      // case "chat":
      //   return <Chat data={} />;
      // case "settings":
      //   return <Settings data={settingsData} />;
      default:
        return <h2>Welcome to your dashboard</h2>;
    }
  };

  return (
    <Layout menuItems={menuItems} onMenuClick={setActiveComponent}>
      {renderComponent()}
    </Layout>
  );
};

export default DoctorDashboard;
