import axios from "axios";

const DASHBOARD_API_BASE_URL = "http://127.0.0.1:8000/api/dashboard";
const DOCTOR_API_BASE_URL = "http://127.0.0.1:8000/api/doctor";

// Fetch dashboard data for a user or doctor
export const fetchDashboardData = async (userType) => {
  try {
    const endpoint = userType === "doctor" ? "dashboard/doctor/" : "dashboard/user/";
    const response = await axios.get(`${DASHBOARD_API_BASE_URL}/${endpoint}`, {
      headers: {
        Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
      },
    });
    return response.data;
  } catch (error) {
    throw error.response?.data || "Failed to fetch dashboard data.";
  }
};

// Fetch doctor profile data
export const fetchDoctorProfile = async (profileId) => {
  try {
    const response = await axios.get(`${DOCTOR_API_BASE_URL}/doctor-profiles/${profileId}/`, {
      headers: {
        Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
      },
    });
    return response.data;
  } catch (error) {
    throw error.response?.data || "Failed to fetch doctor profile.";
  }
};
