import React, { useState, useEffect } from "react";
import { getDoctorProfile } from "../services/api";
import "../styles/Profile.css";  // Importing the CSS file for styling

const Profile = () => {
  const [profile, setProfile] = useState(null);
  const [error, setError] = useState("");

  const fetchProfile = async () => {
    console.log("Fetching profile data...");
    try {
      const profileData = await getDoctorProfile();  // Fetching the profile data
      console.log("Fetched Profile Data:", profileData);
      setProfile(profileData);  // Setting the profile data state
    } catch (error) {
      console.error("Error fetching profile:", error);
      setError("Failed to load profile. Please try again.");
    }
  };

  useEffect(() => {
    console.log("Component mounted, calling fetchProfile...");
    fetchProfile();  // Fetch profile when component mounts
  }, []);

  // Log profile data whenever it changes
  useEffect(() => {
    if (profile) {
      console.log("Profile data updated:", profile);
    }
  }, [profile]);

  // Use a local placeholder image if profile_pic is not available
  const profilePicUrl = profile?.profile_pic || "/images/placeholder.png"; // Local placeholder image

  return (
    <div className="profile">
      {error && <p className="error">{error}</p>}
      {profile ? (
        <div>
          <div className="profile-header">
            <div className="profile-pic-wrapper">
              <img
                src={profilePicUrl}
                alt="Profile Pic"
                className="profile-pic"
              />
            </div>
            <h2>{profile.name}</h2>
          </div>
          <div className="profile-info">
            <p className="left">Degree: </p>
            <p>{profile.degree}</p>
            <p className="left">Email: </p>
            <p>{profile.email}</p>
            <p className="left">Phone: </p>
            <p>{profile.phone_number}</p>
            <p className="left">City: </p>
            <p>{profile.city}</p>
            <p className="left">Description: </p>
            <p>{profile.description}</p>
            <p className="left">Hospital: </p>
            <p>{profile.hospital}</p>
            <p className="left">Experience: </p>
            <p>{profile.experience} years</p>
            <p className="left">Fees: </p>
            <p>{profile.fees}</p>
            <p className="left">Top Expertise: </p>
            <p>{profile.top_expertise}</p>
            <p className="left">Rating: </p>
            <p>{profile.rating}</p>
            <p className="left">Address: </p>
            <p>{profile.address}</p>
            <p className="left">Area: </p>
            <p>{profile.area}</p>
          </div>
        </div>
      ) : (
        <p>Loading profile...</p>
      )}
    </div>
  );
};

export default Profile;
